export type ContainerWidth = 'fluid' | 'extended' | 'wide' | 'medium' | 'narrow'

export type AlignItemsValue =
  | 'flex-start'
  | 'flex-center'
  | 'flex-end'
  | 'baseline'
  | 'stretch'

  
export type GutterAxisValue = 'x' | 'y' | 'xy'

